mapper.select:
SELECT ARTICLENO 
FROM SHOP_BOARD
WHERE USERID=#{userId} and  TITLE=#{title}


SELECT articleNO, title, content, userId, writeDate, viewCnt			
FROM shop_board
WHERE articleNO = #{articleNO}

mapper.update:

UPDATE SHOP_BOARD
SET VIEWCNT = viewCnt + 1
WHERE ARTICLENO = #{articleNO}


UPDATE SHOP_BOARD 
SET
	USERID=#{userId},
	TITLE = #{title},
	CONTENT = #{content}
WHERE ARTICLENO = #{articleNO}
