mapper.select:
SELECT * FROM ( SELECT level, re_articleNO, re_parentNO, LPAD(' ', 4*(level-1)) || re_title re_title, re_content, userId, re_writeDate, re_viewCnt, rownum rno FROM SHOP_REVIEW WHERE re_parentNO=0 START WITH re_parentNO=0 CONNECT BY PRIOR re_articleNO=re_parentNO ORDER BY re_viewCnt DESC ) WHERE ROWNUM<=3
UNION ALL
SELECT * FROM ( SELECT level, re_articleNO, re_parentNO, LPAD(' ', 4*(level-1)) || re_title re_title, re_content, userId, re_writeDate, re_viewCnt, rownum rno FROM shop_review WHERE re_articleNO>=0 START WITH re_parentNO=0 CONNECT BY PRIOR re_articleNO=re_parentNO ORDER SIBLINGS BY DECODE(re_viewCnt,'max(re_noti)',1), re_articleNO DESC ) WHERE rno BETWEEN #{rowStart} and #{rowEnd}

mapper.update:
UPDATE shop_order
SET reviewStatus = 1
WHERE ORDERNUM = #{re_orderNum}

mapper.select:
SELECT re_articleNO 
from shop_review 
WHERE userId=#{userId} and  re_title=#{re_title}


SELECT re_title, userId, re_content, re_writeDate, re_viewCnt, re_articleNO, re_fakeOrderNum, re_orderNum
FROM shop_review
WHERE re_articleNO = #{re_articleNO}

mapper.update:

UPDATE shop_review
SET re_viewCnt = re_viewCnt + 1
WHERE re_articleNO = #{re_articleNO}


UPDATE shop_review 
SET
	userId=#{userId},
	re_title = #{re_title},
	re_content = #{re_content}
WHERE re_articleNO = #{re_articleNO}
